import React from 'react';
import { SafeAreaView, Text, ImageBackground, View, StyleSheet, TouchableOpacity } from 'react-native';
import welcome from '../assets/images/welcome.jpeg';


export default function Welcomer() {
  return (
    <SafeAreaView style={styles.container}>
      <ImageBackground source={welcome} style={styles.imagebg}>
        <View style={styles.body}>
          <View>
          <Text style={styles.bodytxt}>
            Explore the world
          </Text></View>
          <TouchableOpacity style={styles.button}>
            <Text style={styles.buttontxt}>Get started</Text>
          </TouchableOpacity>
        </View>
        </ImageBackground>
    </SafeAreaView>
  );
}


const styles = StyleSheet.create({
  container: {
    display: 'flex',
    height: '100%',
    width: '100%'
  },

  imagebg: {
    height: '100%',
    width: '100%',
  },

  body: {
    position: 'absolute',
    bottom: '20%',
    alignItems:'center',
    justifyContent:'center'
  },

  bodytxt: {
    color: '#fff',
    fontSize: 40,
    fontWeight: 'bold',
  textAlign: 'center'
  }
})
