import React from "react";
import {
  SafeAreaView,
  Text,
  View,
  TouchableOpacity,
  StyleSheet,
  FlatList,
} from "react-native";
import { Ionicons } from "@expo/vector-icons";

export default function Home({ navigation }) {
  const teams = [
    {
      id: 1,
      name1: "Chelsea",
      name2: "Mancity",
      time: "07:30",
      date: "31 OCT",
      location: "Emirates Stadium, London",
    },
    {
      id: 2,
      name1: "Arsenal",
      name2: "Mancity",
      time: "07:30",
      date: "31 OCT",
      location: "Emirates Stadium, London",
    },

  ];

  function renderTeams() {
    const renderItem = ({ item }) => {
      return (
        <View>
        <TouchableOpacity
          style={{
            backgroundColor: "#fff",
            marginTop: 40,
            height: "50%",
            padding: 5,
            justifyContent: "center",
            width: "90%",
            alignSelf: "center",
            borderRadius: 25,
            ...styles.shadow,
          }}
        >
          <View
            style={{
              flexDirection: "row",
              justifyContent: "space-evenly",
              alignItems: "center",
              marginTop: -20
            }}
          >
            <Text style={{ fontSize: 20, fontWeight: "500" }}>
              {item.name1}
            </Text>
            <View style={{ justifyContent: "space-evenly", height:70 , alignItems:'center'
            }}>
              <Text style={{ fontSize: 16, fontWeight: "bold", color:'red' }}>{item.time}</Text>
              <Text style={{ fontSize: 17, color:'grey' }}>{item.date}</Text>
            </View>
            <Text style={{ fontSize: 20, fontWeight: "500" }}>{item.name2}</Text>
          </View>
          <Text style={{ fontSize: 18, alignSelf: "center", marginTop: 10,}}>{item.location}</Text>
        </TouchableOpacity></View>
      );
    };

    return (
      <FlatList
        data={teams}
        keyExtractor={(item) => item.id}
        renderItem={renderItem}
        contentContainerStyle={{
          paddingHorizontal: 20,
          paddingBottom: 30,
        }}
      />
    );
  }

  const renderHeader = () => {
    return (
      <View>
        <View
          style={{
            flexDirection: "row",
            justifyContent: "space-between",
            width: "55%",
            alignItems: "center",
            marginLeft: 25,
            marginTop: 10,
          }}
        >
          <TouchableOpacity onPress={() => navigation.goBack()}>
            <Ionicons name="chevron-back" size={34} color="black" />
          </TouchableOpacity>
          <Text
            style={{
              fontSize: 25,
              fontWeight: "bold",
            }}
          >
            Matchs
          </Text>
        </View>
        <View
          style={{
            marginTop: 25,
            backgroundColor: "lightgrey",
            height: 50,
            padding: 10,
            justifyContent: "center",
            width: "80%",
            alignSelf: "center",
            borderRadius: 25,
          }}
        >
          <TouchableOpacity
            style={{
              backgroundColor: "yellow",
              height: 40,
              padding: 8,
              justifyContent: "center",
              alignItems: "center",
              width: "100%",
              borderRadius: 25,
            }}
          >
            <Text style={{ color: "blue", fontSize: 20, fontWeight: "600" }}>
              Information
            </Text>
          </TouchableOpacity>
        </View>
      </View>
    );
  };

  return (
    <SafeAreaView style={styles.container}>
      {renderHeader()}
      {renderTeams()}
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    display: "flex",
    height: "100%",
    width: "100%",
  },

  shadow: {
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 3,
    },
    shadowOpacity: 0.1,
    shadowRadius: 3,
    elevation: 1,
  },
});
