    fontWeight: "bold",
    marginTop: "40%",
    marginLeft: 50,
  },

  login: {
    fontSize: 40,
    fontWeight: "bold",
    marginLeft: 50,
  },

  section: {
    marginLeft: 50,
    flexDirection: "row",
    marginTop: 30,
  },

  sectiontxt: {
    color: "grey",
    fontSize: 20,
  },

  agencyinput: {
    width: "80%",
    padding: 20,
    backgroundColor: "navajowhite",
    marginTop: 50,
    alignSelf: "center",
    borderRadius: 10,
    flexDirection: "row",
    justifyContent: "space-between",
  },

  passwordinput: {
    width: "80%",
    padding: 30,
    backgroundColor: "lightgrey",
    marginTop: 20,
    alignSelf: "center",
    borderRadius: 10,
  },

  loginbtn: {
    width: "80%",
    padding: 30,
    backgroundColor: "firebrick",
    marginTop: '23%',
    alignSelf: "center",
    borderRadius: 10,
  },

  logintxt: {
    color: "#fff",
    fontSize: 30,
  }
});
